mapper.select:

  select project_name as projectName
	from user, crew_user, crew, project 
	where user.no=#{userNo} and user.no=crew_user.user_no and crew_user.crew_no = crew.no and crew.no = project.crew_no;


  select count(*)
	from user, crew_user, crew, project 
	where user.no=#{userNo} and user.no=crew_user.user_no and crew_user.crew_no = crew.no and crew.no = project.crew_no;
