mapper.select:

select a.no as id, a.project_name as projectName, a.start_date as start, a.end_date as end, a.descrition as description, a.status, c.name as crewName,  c.no as crewNo
from project a, channel b, crew c, crew_user d
where b.no= #{channelNo} and a.enable=1 and a.crew_no = c.no and b.no = c.channel_no and c.no=d.crew_no and d.user_no=#{userNo};


select count(project_name) from project 
	where crew_no = #{crewNo } 
and project_name = #{projectName };
